import * as actionTypes from './actions';

const initialState={
    ingredients:{
        salad:0,
        bacon:0,
        cheese:0,
        meat:0
    },
        totalPrice:4,
};

const reducer=(state=initialState,action)=>{
        switch(action.type){
            case actionTypes.ADD_INGREDIENTS:
                return{
                    ...state,
                    ingredients:{
                        ...state.ingredients
                    }
                };
