export const addIngredient= (name)=>{
    return {
        type:actionTypes.ADD_INGREDIENTS,
        ingredientName:name
    };
};

export const removeIngredient= (name)=>{
    return {
        type:actionTypes.REMOVE_INGREDIENTS,
        ingredientName:name
    };
};

export const setIngredients=(ingredients)=>{
        return{
