import React, { Component } from 'react';
import Layout from './hoc/Layouts/Layout';
import BurgerBuilder from './containers/BurgerBuilder/BurgerBuilder';
import './App.css';

class App extends Component {

  render() {
    return (
      <div>
        <Layout>
        {this.state.show?<BurgerBuilder/>:null}  
        </Layout>
      </div>
    );
  }
}

export default App;
